mapper.update:
UPDATE NMJ_member 
SET mb_type = -1 
WHERE mb_uid = #{param1}

UPDATE NMJ_review 
SET review_content = "관리자에 의해 삭제된 글입니다."
WHERE review_uid = #{param1}

mapper.select:
 		SELECT a.*, b.mb_uid
 		FROM NMJ_store a JOIN NMJ_member b ON a.mb_uid = b.mb_uid 
		WHERE store_uid = #{param1}
