mapper.select:
SELECT mb_img_sav, mb_tel, mb_name, mb_id, mb_pw, mb_email
FROM NMJ_member
WHERE mb_uid = #{mb_uid};

mapper.update:
UPDATE NMJ_member
SET mb_name = #{dto.mb_name}, mb_tel = #{dto.mb_tel}, mb_pw = #{dto.mb_pw},
mb_email = #{dto.mb_email}, mb_img_sav = #{dto.mb_img_sav}
WHERE mb_uid = #{dto.mb_uid};

mapper.select:

SELECT n3.store_type, n3.store_name, n2.review_content, n2.review_rate, n2.review_viewCount, n2.review_date, n2.review_uid
FROM NMJ_member n1, NMJ_review n2, NMJ_store n3
WHERE n2.mb_uid = #{param1}
AND n1.mb_uid = n2.mb_uid
AND n2.store_uid = n3.store_uid;
